require 'colorize'

puts "  Start seeding seeds.db".green

# seeding adminuser
# because of gem 'devise' the encrypted password has to be stored seperately
# after using devises User.create
puts "    seeding user: username: admin".green
User.create!([
  {username:  ENV["ADMIN_USERNAME"], email: ENV["ADMIN_EMAIL"], password: ENV["ADMIN_PASSWORD"], password_confirmation: ENV["ADMIN_PASSWORD"]}
])

puts "    seeding settings".green
Setting.create!([
  {name: "application_name", description: "Name of the application. Will be used in the title", value: "Certman"},
  {name: "autogenerated_keysize", description: "Size of autogenerated openssl keys. Use 1024, 2048 or 4096", value: "4096"},
  {name: "send_with_gmail", description: "If set to 'true', emails will be sent with Gmail", value: ENV["SEND_WITH_GMAIL"]},
  {name: "gmail_username", description: "Gmail account which will be used to send email for new user invitations", value: ENV["GMAIL_USERNAME"]},
  {name: "gmail_password", description: "Password for gmail_username", value: ENV["GMAIL_PASSWORD"]}
])

puts "    seeding certificate-types".green
Certificatetype.create!([
  {name: "Root CA Certificate"},
  {name: "Intermediate CA Certificate"},
  {name: "SSL Certificate"},
  {name: "SSL Wildcard Certificate"},
  {name: "SSL Certificate (selfigned)"},
